import{_ as e,c as i,o as t,aN as l}from"./chunks/framework.CMIDsdwV.js";const u=JSON.parse('{"title":"消息队列介绍","description":"","frontmatter":{"title":"消息队列介绍","tags":["架构","消息队列"],"categories":["架构"]},"headers":[],"relativePath":"Java/架构设计/分布式/03.分布式消息队列/1_消息队列介绍.md","filePath":"Java/架构设计/分布式/03.分布式消息队列/1_消息队列介绍.md","lastUpdated":1767444225000}'),r={name:"Java/架构设计/分布式/03.分布式消息队列/1_消息队列介绍.md"};function o(n,a,d,s,_,h){return t(),i("div",null,[...a[0]||(a[0]=[l('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /Java\\架构设计\\分布式\\03.分布式消息队列\\1_消息队列介绍.md for this page in Markdown format</div><h1 id="消息队列介绍" tabindex="-1">消息队列介绍 <a class="header-anchor" href="#消息队列介绍" aria-label="Permalink to &quot;消息队列介绍&quot;">​</a></h1><h3 id="🌖-消息队列" tabindex="-1">🌖 消息队列 <a class="header-anchor" href="#🌖-消息队列" aria-label="Permalink to &quot;🌖 消息队列&quot;">​</a></h3><p>消息队列是用于传输和保存消息的容器，也是大型分布式系统中常用的技术，主要解决应用耦合、异步消息、流量削锋等问题。后台开发必学，也是面试重点。</p><h4 id="知识" tabindex="-1">知识 <a class="header-anchor" href="#知识" aria-label="Permalink to &quot;知识&quot;">​</a></h4><ul><li>消息队列的作用</li><li>RabbitMQ 消息队列 <ul><li>生产消费模型</li><li>交换机模型</li><li>死信队列</li><li>延迟队列</li><li>消息持久化</li><li>Java 操作</li><li>集群搭建</li></ul></li><li>相关技术：ActiveMQ、RocketMQ、RabbitMQ、Kafka、TubeMQ</li></ul><h4 id="学习建议" tabindex="-1">学习建议 <a class="header-anchor" href="#学习建议" aria-label="Permalink to &quot;学习建议&quot;">​</a></h4><p>和缓存一样，学会如何使用消息队列并不难，无非就是调用 API 去生产、转发和消费消息。</p><p>因此，建议先能够独立使用它，了解消息队列的应用场景；再学习如何在 Java 中操作消息队列中间件，并尝试和项目相结合，感受消息队列带来的好处。</p><p>这里我建议初学者先学习 RabbitMQ，比 Kafka 要好理解一些。跟着视频教程实操一遍即可，可以等到面试前再去深入了解原理和高级特性。</p><h4 id="经典面试题" tabindex="-1">经典面试题 <a class="header-anchor" href="#经典面试题" aria-label="Permalink to &quot;经典面试题&quot;">​</a></h4><ol><li>使用消息队列有哪些优缺点？</li><li>如何保证消息消费的幂等性？</li><li>消息队列有哪些路由模型？</li><li>你是否用过消息队列，解决过什么问题？</li></ol>',12)])])}const p=e(r,[["render",o]]);export{u as __pageData,p as default};
