import{_ as a,c as n,o as s,aN as r}from"./chunks/framework.CMIDsdwV.js";const l="/assets/1642754357819088619.wj2NhsZx.png",b=JSON.parse('{"title":"openEuler","description":"","frontmatter":{"title":"openEuler","tags":["Linux"],"categories":["Linux"]},"headers":[],"relativePath":"Linux/05.OpenEuler/1_openEuler.md","filePath":"Linux/05.OpenEuler/1_openEuler.md","lastUpdated":1769765079000}'),p={name:"Linux/05.OpenEuler/1_openEuler.md"};function t(o,e,i,u,c,d){return s(),n("div",null,[...e[0]||(e[0]=[r(`<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /Linux\\05.OpenEuler\\1_openEuler.md for this page in Markdown format</div><h1 id="openeuler" tabindex="-1">openEuler <a class="header-anchor" href="#openeuler" aria-label="Permalink to &quot;openEuler&quot;">​</a></h1><h2 id="一、openeuler简介" tabindex="-1">一、OpenEuler简介 <a class="header-anchor" href="#一、openeuler简介" aria-label="Permalink to &quot;一、OpenEuler简介&quot;">​</a></h2><blockquote><p>欧拉系统官网：<a href="https://www.openeuler.openatom.cn/zh/" target="_blank" rel="noreferrer">https://www.openeuler.openatom.cn/zh/</a></p></blockquote><h3 id="_1、什么是欧拉" tabindex="-1">1、什么是欧拉 <a class="header-anchor" href="#_1、什么是欧拉" aria-label="Permalink to &quot;1、什么是欧拉&quot;">​</a></h3><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>欧拉是数字基础设施的开源操作系统，可广泛部署于服务器、云计算、边缘计算、嵌入式等各种形态设备，应用场景覆盖IT（Information Technology）、CT（Communication Technology）和OT（Operational Technology），实现统一操作系统支持多设备，应用一次开发覆盖全场景。</span></span>
<span class="line"><span></span></span>
<span class="line"><span>openEuler操作系统开源以来，获得了产业界的积极响应，已经有三百家企业、近万名社区开发者加入，成为中国极具活力的开源社区，目前欧拉商用已经突破60万套。2021年9月，华为全新升级欧拉，从服务器操作系统，升级为数字基础设施的操作系统，支持IT、CT、OT等数字基础设施全场景，覆盖服务器、云计算、边缘计算、嵌入式等各种形态的设备。</span></span>
<span class="line"><span></span></span>
<span class="line"><span>目前，欧拉和鸿蒙实现了内核技术共享，未来将进一步在分布式软总线、安全OS、设备驱动框架、以及新编程语言等方面实现共享。通过能力共享，实现生态互通及云边端协同，更好地服务数字化全场景。</span></span>
<span class="line"><span></span></span>
<span class="line"><span>欧拉的定位是瞄准国家数字基础设施的操作系统和生态底座。捐赠给开放原子开源基金会，汇聚更多产业力量，对于打造数字中国坚实底座具有重要价值，欧拉也将成为全产业共同拥有的开源生态。为了更好的推动数字区域数字经济发展，华为联合北京、广州、深圳、成都、武汉、南京全国6大城市、协同8家操作系统伙伴共同启动首批“欧拉生态创新中心”，进一步深化欧拉生态的全国布局。</span></span>
<span class="line"><span></span></span>
<span class="line"><span>                                                            ​ ——华为云开发者社区</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br></div></div><h3 id="_2、欧拉的优势" tabindex="-1">2、欧拉的优势 <a class="header-anchor" href="#_2、欧拉的优势" aria-label="Permalink to &quot;2、欧拉的优势&quot;">​</a></h3><p><a href="https://bbs.huaweicloud.com/blogs/327740" target="_blank" rel="noreferrer">https://bbs.huaweicloud.com/blogs/327740</a></p><p><img src="`+l+'" alt="image.png" loading="lazy"></p><h2 id="二、openeuler安装" tabindex="-1">二、OpenEuler安装 <a class="header-anchor" href="#二、openeuler安装" aria-label="Permalink to &quot;二、OpenEuler安装&quot;">​</a></h2><p>vmware安装openEuler：<a href="https://blog.csdn.net/qq_45945548/article/details/120520237" target="_blank" rel="noreferrer">https://blog.csdn.net/qq_45945548/article/details/120520237</a></p><p>安装openEuler可视化界面：<a href="https://blog.csdn.net/qq_50824019/article/details/124526889" target="_blank" rel="noreferrer">https://blog.csdn.net/qq_50824019/article/details/124526889</a></p>',12)])])}const m=a(p,[["render",t]]);export{b as __pageData,m as default};
