import{_ as e,c as i,o as t,ah as s,qt as l}from"./chunks/framework.D5cOWG0Y.js";const f=JSON.parse('{"title":"LORA原理与训练","description":"","frontmatter":{"title":"LORA原理与训练","date":"2024-02-22","tags":["Stable Diffusion"],"categories":["Stable Diffusion"]},"headers":[],"relativePath":"StableDiffusion/StableDiffusion/4_LORA训练.md","filePath":"StableDiffusion/StableDiffusion/4_LORA训练.md","lastUpdated":1756054686000}'),o={name:"StableDiffusion/StableDiffusion/4_LORA训练.md"};function r(n,a,p,h,d,c){return t(),i("div",null,[...a[0]||(a[0]=[s('<h2 id="kohya训练器简介" tabindex="-1">Kohya训练器简介 <a class="header-anchor" href="#kohya训练器简介" aria-label="Permalink to “Kohya训练器简介”">​</a></h2><p>Kohya GUI的核心是由开源社区开发者@Kohya.S 基于Simo Ryu（@cloneofsimo）的发现编写的LoRA训练脚本sd-scripts 。这是SD社区里出现最早的LoRA训练脚本，后续的大多数训练器与训练脚本均是在这个脚本的基础上衍生开发的。</p><p>我们使用的Kohya训练器，来源于另一位开发者@bmaltais 在Kohya脚本的基础上使用Gradio制作的图形界面（GUI），内置了可视化的界面帮助我们合理地输入、调整各项参数，并整合了参数预设、数据整理打标等在内的一系列配套功能（虽然我不常用kk）。</p><h3 id="下载训练器" tabindex="-1">下载训练器 <a class="header-anchor" href="#下载训练器" aria-label="Permalink to “下载训练器”">​</a></h3><p>新建一个空文件夹目录。清空路径栏并输入“cmd”，回车调出命令行；或右键使用git bash命令行。</p><p>输入项目克隆命令</p><div class="language- line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>git clone https://github.com/bmaltais/kohya_ss.git</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p>等待下载完成。</p><h3 id="运行初始化" tabindex="-1">运行初始化 <a class="header-anchor" href="#运行初始化" aria-label="Permalink to “运行初始化”">​</a></h3><p>初次启动训练器，运行根目录下的“setup.bat”文件。</p><p>初次启动，会弹出选项界面。输入选项对应数字回车，完成选择。</p><p><img src="'+l+'" alt="image-20240302004304933" loading="lazy"></p><p>选择1，安装必须依赖。</p><h2 id="参考资料" tabindex="-1">参考资料 <a class="header-anchor" href="#参考资料" aria-label="Permalink to “参考资料”">​</a></h2><p><a href="https://www.bilibili.com/video/BV16e411e7Zx/" target="_blank" rel="noreferrer">60分钟速通LORA训练！绝对是你看过最好懂的AI绘画模型训练教程！StableDiffusion超详细训练原理讲解+实操教学，LORA参数详解与训练集处理技巧_哔哩哔哩_bilibili</a></p>',15)])])}const _=e(o,[["render",r]]);export{f as __pageData,_ as default};
