import{_ as i,c as l,o as r,ah as e,uF as t,uG as s,uH as p,uI as n,uJ as o}from"./chunks/framework.D5cOWG0Y.js";const b=JSON.parse('{"title":"标准化和软件知识产权基础知识","description":"","frontmatter":{"title":"标准化和软件知识产权基础知识","tags":["中级软件设计师"],"categories":["中级软件设计师"]},"headers":[],"relativePath":"daily/软考中级软件设计师学习笔记/11_标准化和软件知识产权基础知识.md","filePath":"daily/软考中级软件设计师学习笔记/11_标准化和软件知识产权基础知识.md","lastUpdated":1756054686000}'),_={name:"daily/软考中级软件设计师学习笔记/11_标准化和软件知识产权基础知识.md"};function d(h,a,c,g,m,u){return r(),l("div",null,[...a[0]||(a[0]=[e('<h2 id="知识产权基础知识" tabindex="-1">知识产权基础知识 <a class="header-anchor" href="#知识产权基础知识" aria-label="Permalink to “知识产权基础知识”">​</a></h2><h3 id="保护期限" tabindex="-1">保护期限 <a class="header-anchor" href="#保护期限" aria-label="Permalink to “保护期限”">​</a></h3><p><img src="'+t+'" alt="image.png" loading="lazy"></p><h3 id="知识产权人的确定" tabindex="-1">知识产权人的确定 <a class="header-anchor" href="#知识产权人的确定" aria-label="Permalink to “知识产权人的确定”">​</a></h3><p>单位和个人的<strong>著作权</strong>归属情况：</p><p><img src="'+s+'" alt="image.png" loading="lazy"></p><p>单位和委托的区别在于，<strong>当合同中未规定著作权的归属时，著作权默认归于单位，而委托创作中，著作权默认归属于创作方个人</strong>，具体如下：</p><p><img src="'+p+'" alt="image.png" loading="lazy"></p><h3 id="侵权判定" tabindex="-1">侵权判定 <a class="header-anchor" href="#侵权判定" aria-label="Permalink to “侵权判定”">​</a></h3><p><img src="'+n+'" alt="img" loading="lazy"></p><p><img src="'+o+'" alt="在这里插入图片描述" loading="lazy"></p><h2 id="标准化基础知识" tabindex="-1">标准化基础知识 <a class="header-anchor" href="#标准化基础知识" aria-label="Permalink to “标准化基础知识”">​</a></h2><h3 id="标准的分类" tabindex="-1">标准的分类 <a class="header-anchor" href="#标准的分类" aria-label="Permalink to “标准的分类”">​</a></h3><ul><li><p>国际标准：ISO、IEC等国际标准化组织</p></li><li><p>国家标准：GB-中国、ANSI-美国、BS-英国、JIS-日本</p></li><li><p>区域标准：又称为地区标准，如PASC-太平洋地区标准会议、CEN-欧洲标准委员会、ASAC-亚洲标准咨询委员会、ARSO—非洲地区标准化组织</p></li><li><p>行业标准：GJB-中国军用标准、MIT-S-美国军用标准、IEEE-美国电气电子工程师协会</p></li><li><p>地方标准：国家的地方一级行政机构制订的标准</p></li><li><p>企业标准</p></li><li><p>项目规范</p></li></ul><h3 id="标准的编号" tabindex="-1">标准的编号 <a class="header-anchor" href="#标准的编号" aria-label="Permalink to “标准的编号”">​</a></h3><ul><li><p>国际：标准代号+专业类号+顺序号+年代号。</p></li><li><p>我国标准代号及编号：</p><ul><li><strong>强制性国家标准代号为GB，</strong></li><li><strong>推荐性国家标准代号为GB/T，</strong></li><li><strong>指导性国家标准代号为GB/Z，</strong></li><li><strong>国家实物标准代号GSB</strong>。</li></ul></li><li><p>行业标准代号：一般由汉语拼音大写字母组成，如汽车QC等。</p></li><li><p>地方标准代号：由大写汉语拼音DB加上省级行政区划代码的前两位组成。</p></li><li><p>企业标准代号：由“Q/”加上企业代号组成。</p></li></ul>',16)])])}const S=i(_,[["render",d]]);export{b as __pageData,S as default};
